# Add vagrant user & ssh key
useradd vagrant
echo 'vagrant:vagrant' | chpasswd 
echo "vagrant ALL=(ALL) NOPASSWD: ALL" >> /etc/sudoers
mkdir /home/vagrant/.ssh
echo "ssh-rsa  vagrant insecure public key" > /home/vagrant/.ssh/authorized_keys
chown vagrant:vagrant /home/vagrant/.ssh -R
chmod 700 /home/vagrant/.ssh
chmod 600 /home/vagrant/.ssh/authorized_keys

# VBoxGuestAdditions Setup
cd /tmp
mkdir /tmp/isomount
mount -t iso9660 -o loop /root/VBoxGuestAdditions.iso /tmp/isomount

# Install the drivers
/tmp/isomount/VBoxLinuxAdditions.run

# Cleanup
umount isomount
rm -rf isomount /root/VBoxGuestAdditions.iso

systemctl disable initial-setup-text

# Zero out the rest of the free space using dd, then delete the written file.
dd if=/dev/zero of=/EMPTY bs=1M
rm -f /EMPTY

# Add `sync` so Packer doesn't quit too early, before the large file is deleted.
sync





